import React, { useState } from 'react';
import CategoryTable from '../components/CategoryTable';
import CategoryModal from '../components/CategoryModal';

interface Category {
  id: string;
  name: string; 
  category: string;
  description: string;
  quantity: number;
  photo: string;
}


const CategoryList: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([
    {
      id: '1',
      name: 'Laptop',
      category: 'Electronics',
      description: 'High-performance laptop',
      quantity: 50,
      photo: 'https://img.freepik.com/premium-psd/orange-orange-cool-boys-shoes_1019469-12.jpg?size=626&ext=jpg',
    },
    {
      id: '2',
      name: 'Desk Chair',
      category: 'Furniture',
      description: 'Comfortable desk chair',
      quantity: 20,
      photo: 'https://img.freepik.com/free-photo/pair-trainers_144627-3799.jpg?size=626&ext=jpg',
    },
    {
      id: '3',
      name: 'Headphones',
      category: 'Electronics',
      description: 'Wireless noise-canceling headphones',
      quantity: 30,
      photo: 'https://img.freepik.com/free-photo/fashion-shoes-sneakers_1203-7529.jpg?size=626&ext=jpg',
    },
    {
      id: '4',
      name: 'Sofa',
      category: 'Furniture',
      description: 'Modern sectional sofa',
      quantity: 15,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '5',
      name: 'Coffee Table',
      category: 'Furniture',
      description: 'Glass top coffee table',
      quantity: 25,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '6',
      name: 'Smartphone',
      category: 'Electronics',
      description: 'Latest smartphone with advanced features',
      quantity: 40,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '7',
      name: 'Dining Table',
      category: 'Furniture',
      description: 'Wooden dining table with chairs',
      quantity: 10,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '8',
      name: 'Bluetooth Speaker',
      category: 'Electronics',
      description: 'Portable Bluetooth speaker',
      quantity: 18,
      photo: 'https://media.istockphoto.com/id/1425880214/photo/leather-mens-shoe-on-white-background.webp?b=1&s=170667a&w=0&k=20&c=HsgDQAUh9FyNzEkJo4ZsfreVCVYpOdN5ax2oSZPqFV4=',
    },
    {
      id: '9',
      name: 'Bookshelf',
      category: 'Furniture',
      description: 'Tall wooden bookshelf',
      quantity: 22,
      photo: 'https://media.istockphoto.com/id/1629114862/photo/close-up-of-white-sports-shoes-on-a-white-background.webp?b=1&s=170667a&w=0&k=20&c=xJnzhqYgVTrLE_d3OB5FCAq34UBvYKCq3PgPUkrxjeM=',
    },
    {
      id: '10',
      name: 'Digital Camera',
      category: 'Electronics',
      description: 'Mirrorless digital camera with high resolution',
      quantity: 12,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '1',
      name: 'Laptop',
      category: 'Electronics',
      description: 'High-performance laptop',
      quantity: 50,
      photo: 'data:image/jpeg;base64,',
    },
    {
      id: '2',
      name: 'Desk Chair',
      category: 'Furniture',
      description: 'Comfortable desk chair',
      quantity: 20,
      photo: 'https://thumbs.dreamstime.com/b/blue-shoes-29507491.jpg',
    },
    // Add more sample data...
  ]);

  const [deleteConfirmation, setDeleteConfirmation] = useState<string | null>(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<Category | null>(null);

  // const [filter, setFilter] = useState({ name: '', category: '' });

  const handleDelete = (id: string) => {
    setDeleteConfirmation(id);
  };

  const handleEdit = (editedCategory: Category) => {
    const updatedCategories = categories.map((category) =>
      category.id === editedCategory.id ? editedCategory : category
    );
    setCategories(updatedCategories);
  };

  const handleAdd = (newCategory: Category) => {
    const id = (categories.length + 1).toString();
    const updatedCategories = [...categories, { ...newCategory, id }];
    setCategories(updatedCategories);
  };

 

  return (
    <div className="container mx-auto p-6 block">
      <h1 className="text-xl font-semibold mb-6">Category List Page</h1>
      <CategoryTable
  categories={categories}
  onDelete={handleDelete}
  onEdit={handleEdit}
  onAdd={handleAdd} // Make sure to add this line with a function for adding categories
/>


      {/* {renderPageNumbers()} */}

      {deleteConfirmation && (
        <div className="fixed top-0 left-0 w-full h-full bg-gray-800 bg-opacity-75 flex
         items-center justify-center">
          <div className="bg-white p-6 rounded-md w-96">
            <p>{`Are you sure you want to delete category with ID: ${deleteConfirmation}?`}</p>
            <button
              onClick={() => {
                const updatedCategories = categories.filter(
                  (category) => category.id !== deleteConfirmation
                );
                setCategories(updatedCategories);
                setDeleteConfirmation(null);
              }}
              className="yes px-4 py-2 rounded-md mr-2 hover:bg-red-700"
            >
              Yes
            </button>
            <button
              onClick={() => setDeleteConfirmation(null)}
              className="cancel px-4 py-2 rounded-md hover:bg-gray-400"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {modalOpen && (
        <CategoryModal
          category={selectedCategory}
          onClose={() => {
            setModalOpen(false);
            setSelectedCategory(null);
          }}
          onSave={(editedCategory) => {
            if (selectedCategory) {
              handleEdit(editedCategory);
            } else {
              handleAdd(editedCategory);
            }
            setModalOpen(false);
            setSelectedCategory(null);
          }}
        />
      )}
    </div>
  );
};

export default CategoryList;















